import React from "react";

const range = props => {
    const styles = {
        formGroup: {
            width: "100%"
        },
        range: {
            margin: "0px"
        }
    };

    return (
        <div className="form-group" style={styles.formGroup}>
            <input 
            style={styles.range}
            className="custom-range"
            type="range"
            min={props.min || 0}
            max={props.max || 100}
            step={props.step || 1}/>
        </div>
    );
};

export default range;
