import React from "react";

const grid = props => {
    const styles = {
        display: "grid",
        gridTemplateColumns: `repeat(${props.columns}, 1fr)`,
        gridColumnGap: "1.5rem",
        gridRowGap: "1rem",
        padding: "0.5rem"
    };

    return (
        <div style={styles}>
            {props.children}
        </div>
    );
};

export default grid;
