import React, { 
  Component
} 
from 'react';

import DrumMachine from "./components/drumMachine"

import {
  keys
}
from "./globals";

//this.audio.volume = (1 - 0); 0 är lägst, 1 är högst
//this.audio.muted = false;
//this.audio.defaultMuted = false;

class App extends Component {
  componentDidMount() {
    this.pressedKey = "";
    this.audio = new Audio('https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3');

    document.addEventListener("keydown", event => {
      this.pressedKey = event.key;
      this.audio.play();

      console.log(this.audio.played.start);
    })
  }

  onClickDrum = key => {
    key = key.toLowerCase();
    this.pressedKey = keys[key];
    console.log(this.pressedKey)
  }

  render() {
    return (
      <DrumMachine
      onVolumeChange={e => console.log(`The volume is ${e.target.value}`)}
      soundType="abra"

      onClickPower={() => console.log("Power")}
      onClickBank={() => console.log("Bank")}

      onClickQ={() => this.onClickDrum("Q")}
      onClickW={() => this.onClickDrum("W")}
      onClickE={() => this.onClickDrum("E")}

      onClickA={() => this.onClickDrum("A")}
      onClickS={() => this.onClickDrum("S")}
      onClickD={() => this.onClickDrum("D")}

      onClickZ={() => this.onClickDrum("Z")}
      onClickX={() => this.onClickDrum("X")}
      onClickC={() => this.onClickDrum("C")}/>
    );
  }
}

export default App;
