import React from "react";

const grid = props => {
    const styles = {
        display: "grid",
        gridTemplateColumns: "repeat(3, 1fr)",
        gridColumnGap: "3rem"
    };

    return (
        <div style={styles}>
            {props.children}
        </div>
    );
};

export default grid;
