import React from "react";

const button = props => {
    let styles = {
        textTransform: "uppercase",
        margin: "0px"
    };

    if (props.style) {
        styles = {...styles, ...props.style};
    }

    return (
        <button 
        className="btn btn-dark btn-lg btn-block" 
        onClick={props.onAction} 
        style={{styles}}>
            {props.name}
        </button>
    );
}

export default button;
