import React from "react";

const range = props => {
    const styles = {
        width: "100%"
    };

    return (
        <div className="form-group">
            <input
            style={styles} 
            class="custom-range"
            type="range"
            min={props.min || 0}
            max={props.max || 100}
            step={props.step || 1}/>
        </div>
    );
};

export default range;
