import React from "react";

const button = props => {
    let styles = {
        textTransform: "uppercase",
        margin: "0px"
    };

    let className = "btn btn-dark btn-lg btn-block";

    if (props.className) {
        className = `${className} ${props.className}`
    }

    return (
        <button 
        className={className}
        onClick={props.onAction} 
        style={styles}>
            {props.name}
        </button>
    );
}

export default button;
