import {
    createAction 
}
from "../functions/createAction";

const togglePower = createAction({
    name: "TOGGLE_POWER",
});

const toggleBank = createAction({
    name: "TOGGLE_BANK",
});

const setVolume = createAction({
    name: "SET_VOLUME",
    payload: [
        "setVolume" 
    ]
});

const setSoundType = createAction({
    name: "SET_SOUND_TYPE",
    payload: [
        "type"
    ]
});

export {
    togglePower,
    toggleBank,
    setVolume
}
