import React, { 
  Component
} 
from 'react';

import DrumMachine from "./components/drumMachine"

class App extends Component {
  componentDidMount() {
    this.pressedKey = "";

    document.addEventListener("keydown", event => {
      this.pressedKey = event.key;
      console.log(this.pressedKey);
    })
  }

  a = key => {
    const keys = {
      q: "q",
      w: "w",
      e: "e",
      a: "a",
      s: "s",
      d: "d",
      z: "z",
      x: "x",
      c: "c"
    }

    this.pressedKey = keys[key];
  }

  render() {
    return (
      <DrumMachine
      onVolumeChange={e => console.log(`The volume is ${e.target.value}`)}
      soundType="abra"

      onClickPower={() => console.log("Power")}
      onClickBank={() => console.log("Bank")}

      onClickQ={() => console.log("Q")}
      onClickW={() => console.log("W")}
      onClickE={() => console.log("E")}

      onClickA={() => console.log("A")}
      onClickS={() => console.log("S")}
      onClickD={() => console.log("D")}

      onClickZ={() => console.log("Z")}
      onClickX={() => console.log("X")}
      onClickC={() => console.log("C")}/>
    );
  }
}

export default App;
