import React, { 
  Component
} 
from 'react';

import DrumMachine from "./components/drumMachine"

import {
  keys
}
from "./globals";

import * as actions from "./store/actions/index";

import {
  connect 
}
from "react-redux";

//this.audio.volume = (1 - 0); 0 är lägst, 1 är högst
//this.audio.muted = false;
//this.audio.defaultMuted = false;

class App extends Component {
  componentDidMount() {
    this.pressedKey = "";

    this.setPressedKey = key => {
      key = key.toLowerCase();
      this.pressedKey = keys[key];
      console.log(this.pressedKey);
    }

    document.addEventListener("keydown", event => {
      this.setPressedKey(event.key);
    })
  }

  render() {
    return (
      <DrumMachine
      onVolumeChange={e => this.props.onSetVolume(e.target.value)}
      volumeValue={this.props.volume}
      soundType="abra"

      onClickPower={() => this.props.onTogglePower()}
      setPowerValue={true}

      onClickBank={() => this.props.onToggleBank()}
      setBankValue={true}

      onClickQ={() => this.setPressedKey("Q")}
      onClickW={() => this.setPressedKey("W")}
      onClickE={() => this.setPressedKey("E")}

      onClickA={() => this.setPressedKey("A")}
      onClickS={() => this.setPressedKey("S")}
      onClickD={() => this.setPressedKey("D")}

      onClickZ={() => this.setPressedKey("Z")}
      onClickX={() => this.setPressedKey("X")}
      onClickC={() => this.setPressedKey("C")}/>
    );
  }
}
const mapStateToProps = state => ({
  volume: state.volume,

});
const mapDispatchToProps = dispatch => ({
  onTogglePower: () => dispatch(actions.togglePower.getAction()),
  onToggleBank: () => dispatch(actions.toggleBank.getAction()),
  onSetVolume: value => dispatch(actions.setVolume.getAction({
    volume: value
  }))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
