import React from "react";

const button = props => {
    const styles = {
        textTransform: "uppercase",
        margin: "0px"
    };

    return (
        <button 
        className="btn btn-dark btn-lg btn-block" 
        onClick={props.onAction} 
        style={styles}>
            {props.name}
        </button>
    );
}

export default button;
