import React, {
    Fragment,
    useRef,
    useEffect
} 
from "react";

const tgButton = props => {
    let inputRef = useRef(null);
    
    useEffect(() => {
        console.log(inputRef.current);
    }, [])

    return (
        <Fragment>
            <input ref={inputRef} onChange={(e) => e.target.value} type="checkbox"/>
        </Fragment>
    );
};

export default tgButton;
