import React, {
    useState
} 
from "react";

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default props => {
    const [state, setState] = useState("");
    const  layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];
      
    return (
        <Jumbotron width="30rem" center>
            <Display text="">
                <Button onClick={props.onDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(button => <Button key={button} name={button}/>)}
            </Grid>
        </Jumbotron>
    );
}
