import {
    onAddChar,
    onDelete,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject,
    concatArrays
}
from "../utilities";

const initState = {
    operations: [],
    temp: "",
    value: 0
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onAddChar.getName: 
            return concatObjects(state, {
                operations: a[char, arr]
            });

        case onAddMath.getName:
            return concatObjects(state, {
                
            });
        
        default:
            return state;
    }
}

function a(char, arr) {
    switch(char) {
        case "+":
        case "-":
        case "X":
        case "/":
            return arr.concat(char)
        default:
            return char;
    }
}
