import React, { 
  Component 
} 
from 'react';

import {
  connect
}
from "react-redux";

import {
  onAddChar
}
from "./store/actions";

import Calculator from "./components/calculator";

class App extends Component {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
  }

  render() {
    return (
      <Calculator
      onClick={() => this.props.onAddChar(1)}
      onClickDelete={() => console.log("delete")}
      onClickEqual={() => console.log("equal")}/>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  onAddChar: value => dispatch(onAddChar.getAction({
    char: value
  }))
});

export default connect(null, mapDispatchToProps)(App);
