import {
    onAddChar,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects
}
from "../utilities";

const initState = {
    string: "",
    currentChar: "",
    value: 0
};

export default (state = initState, action) => {
    switch(action.type) {
        case onAddMath.getName:
            return concatObjects(state, {

            });

        case onAddChar.getName: 
            return concatObjects(state, {
                string: state.string + action.char,
                currentChar: action.char.toString()
            });
        
        default:
            return state;
    }
}
