import React, {
    useState
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

import {
   //getLastItem,
    //getFirstItem
}
from "../utilities";

export default props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    let [state, setState] = useState(""); 

    const stringValidation = string => {
        const regex = /^0|[\/, X, -, +]0+/gi;
        
        string = string.replace(regex, "0");
        
        return string;
    }

    const changeState = value => {
        setState(
            stringValidation(
                `${state}${value}`
            )
        );
    }

    const buttons = value => {
        let button;

        if (!(value === "=")) {
            button = <Button onClick={() => changeState(value)} key={value} name={value}/>;
        }

        else {
            button = <Button onClick={props.onClickEqual} key={value} name={value}/>
        }

        return button;
    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={state}>
                <Button onClick={props.onClickDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>
    );
};
