import React from "react";

export default props => {
    const style = {
        display: "grid",
        gridColumnTemplate: `repeat(1fr, ${props.size || "1"})`,
        gridGap: `${props.gapX} ${props.gapY}`
    }

    return (
        <div style={style}>
            {props.children}
        </div>
    );
}
