import React from "react";

export default props => {
    let classes = ["btn", "btn-lg", "btn-block"];

    const styles = {
        margin: "0px"
    };

    if (props.warning) {
        classes.push("btn-warning")
    }
    else {
        classes.push("btn-primary")
    }

    return (
        <button
        onClick={props.onClick}
        style={styles} 
        type="button" 
        className="btn btn-primary btn-lg btn-block">
            {props.name}
        </button>
    );
}
