import React, { 
  Component 
} 
from 'react';

import {
  Jumbotron,
  VerticalLine,
  Display,
  Button,
  Grid
}
from "./components/bootstrap";

class App extends Component {

  state = {
    string: ""
  }

  layout = [
    7,  8,   9,  "/",
    4,  5,   6,  "X",
    1,  2,   3,  "-",
    0, ".", "=", "+"
  ];

  render() {
    return (
      <Jumbotron width="30rem" center>
        <Display text={this.state.string}>
          <Button name="AC"/>
        </Display>

        <VerticalLine/>    

        <Grid columns={4}>
          {this.layout.map(button => <Button name={button}/>)}
        </Grid>
      </Jumbotron>
    );
  }
}

export default App;
