import {
    onAddChar,
    onDelete,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject
}
from "../utilities";

const initState = {
    string: "",
    currentChar: "",
    value: 0
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onAddMath.getName:
            return concatObjects(state, {
                value: parseInt("1")
            });

        case onAddChar.getName: 
            return concatObjects(state, {
                string: state.string + action.char,
                currentChar: action.char.toString()
            });
        
        default:
            return state;
    }
}
