import {
    onAddChar,
    onDelete,
    onEqual,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject,
}
from "../utilities";
import { stat } from "fs";
import { parse } from "path";

const initState = {
    displayString: "",
    calcString: "",
    value: 0,
    isCalc: false,
    operatorUsed: ""
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onEqual.getName:
            return concatObjects(state, {
                displayString: `${state.displayString}=${state.value + parseFloat(state.calcString)}`,
                calcString: `${state.value + parseFloat(state.calcString)}`,
                value: 0,
                isCalc: true
            })

        case onAddChar.getName: 
            return concatObjects(state, {
                calcString: state.calcString + calcStringValidation(action.char),
                displayString: `${state.displayString}${action.char}`
            });

        case onAddMath.getName:
            console.log(state.value, parseFloat(state.calcString));
            return concatObjects(state, {
                calcString: "",
                value: mathOperator("+", state.value, state.calcString),
                operatorUsed: "+"
            });

        
        default:
            return state;
    }
}

function calcStringValidation(char) {
    switch(char) {
        case "-":
        case "+":
        case "/":
        case "X":
            return ""
        default:
            return char;
    }
}

function mathOperator(type, a, b) {
    a = parseFloat(a);
    b = parseFloat(a)

    console.log(a, b);

    switch(type) {
        case "+":
            return a + b;
            
        case "-":
            return a - b;

        case "/":
            return a / b;

        case "X":
            return a * b;

        default:
            return 0;
    }
}
