import {
    onAddChar,
    onDelete,
    onEqual,
    onAddMath,
    //onSubtractMath,
    //onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject,
}
from "../utilities";

const initState = {
    displayString: "",
    calcString: "",
    value: 0,
    isCalc: false,
    usedOperator: ""
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onEqual.getName:
            if (state.calcString) {
                return concatObjects(initState, {
                    displayString: `${mathOperator(state.usedOperator, state.value, state.calcString)}`,
                    calcString: `${mathOperator(state.usedOperator, state.value, state.calcString)}`,
                })
            }

        case onAddChar.getName: 
            return concatObjects(state, {
                calcString: state.calcString + calcStringValidation(action.char),
                displayString: `${state.displayString}${action.char}`
            });

        case onAddMath.getName:
            return concatObjects(state, {
                calcString: "",
                value: mathOperator("+", state.value, state.calcString),
                usedOperator: "+"
            });

        case onMultipleMath.getName:
            return concatObjects(state, {
                calcString: "",
                value: mathOperator("*", state.value, state.calcString),
                usedOperator: "*"
            });
        
        default:
            return state;
    }
}

function calcStringValidation(char) {
    switch(char) {
        case "-":
        case "+":
        case "/":
        case "X":
            return ""
        default:
            return char;
    }
}

function mathOperator(type, a, b) {
    a = parseFloat(a);
    b = parseFloat(b)

    switch(type) {
        case "+":
            return a + b;
            
        case "-":
            return a - b;

        case "/":
            return a / b;

        case "*":
            a = a === 0? 1: a;
            b = b === 0? b: b; 
            return a * b;

        default:
            return 0;
    }
}
