import {
    onAddChar,
    onDelete,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject,
    concatArrays
}
from "../utilities";

const initState = {
    displayString: "",
    calcStrig: "",
    temp: "",
    value: 0
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onAddChar.getName: 
            return concatObjects(state, {
                calcStrig: state.calcStrig + calcStringValidation(action.char),
                displayString: state.displayString + action.char
            });

        case onAddMath.getName:
            return concatObjects(state, {
                calcStrig: "",
                temp: parseInt(state.calcStrig),
                value: state.value + parseInt(state.calcStrig)
            });
        
        default:
            return state;
    }
}

function calcStringValidation(char) {
    switch(char) {
        case "-":
        case "+":
        case "/":
        case "X":
            return ""
        default:
            return char;
    }
}
