import React, { 
    Component 
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default class extends Component {  
    state = {
        string: ""
    }

    layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    addCharToString(char) {
        this.setState(prevState => ({
            string: `${prevState.string} ${char}`
        }))
    }

    setClickableButton(value) {
        let button = <Button onClick={() => this.addCharToString(value)} key={value} name={value}/>;
                    
        if (button === "=") {
            button = <Button onClick={this.props.onEqual} key={value} name={button}/>
        }

        return button;
    }

    render() {
        return (
        <Jumbotron width="30rem" center>
            <Display text={this.state.string}>
                <Button onClick={this.props.delete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
            {this.layout.map(this.setClickableButton)}
            </Grid>
        </Jumbotron>
        );
    }
}

const a = props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    return (
        <Jumbotron width="30rem" center>
            <Display text={this.state.string}>
                <Button onClick={props.onDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
            {layout.map()}
            </Grid>
        </Jumbotron>

    );
}
