import React, {
    useState
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    let [state, setState] = useState(""); 

    const validation = value => {
        value = value.toString();

        let lastCharOfState = state[state.length - 1];

        switch(lastCharOfState) {
            case "/":
            case "X":
            case "-":
            case "+":
                switch(value) {
                    case "/":
                    case "X":
                    case "-":
                    case "+":
                        value = "";
                    break;
                    default: 
                        return value;
                }
            break;
            default:
                    return value;
        }
    }

    const fn = value => {
        value = validation(value);

        let string = `${state}${value}`;

        string = `${state}${value}`;

        setState(string);
    }

    const buttons = value => {
        let button;

        if (!(value === "=")) {
            button = <Button onClick={() => fn(value)} key={value} name={value}/>;
        }

        else {
            button = <Button onClick={props.onClickEqual} key={value} name={value}/>
        }

        return button;
    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={state}>
                <Button onClick={props.onClickDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>
    );
}
