import {
    onAddChar
}
from "./actions.js";

import {
    concatObjects
}
from "../utilities";

const initState = {
    string: "",
    currentChar: "",
    value: 0
};

export default (state = initState, action) => {
    switch(action.type) {
        case onAddChar.getName: 
            return concatObjects(state, {
                string: state.string + action.char,
                currentChar: action.char.toString()
            });
        
        default:
            return state;
    }
}

function currentChar(char) {
    switch(char) {
        case "-":
        case "+":
        case "/":
        case "X":
            return "";
        
        default:
            return char;
    }
}
