import React from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default React.forwardRef((props, ref) => {
    const layout = [
        7,  8,   9,  " / ",
        4,  5,   6,  " X ",
        1,  2,   3,  " - ",
        0, ".", "=", " + "
    ];

    const buttons = value => {
        let onClickName;

        switch(value) {
            case "/":
                onClickName = "Divide";
            break;
            
            case "X":
                onClickName = "Multiple";
            break;

            case "-":
                onClickName = "Subtract";
            break;

            case "+":
                onClickName = "Add";
            break;

            default:
                onClickName = value;
        }   

        return <Button key={value} name={value} onClick={props[`onClick${oNameString}`]}/>
    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={props.setDisplay}>
                <Button onClick={props.onClickDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>
    );
});
