import React from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    const buttons = value => {
        let onClickName;

        switch(value) {
            case "/":
                onClickName = "Divide";
            break;

            case "X":
                onClickName = "Multiply";
            break;

            case "+":
                onClickName = "Add";
            break;

            case "-":
                onClickName = "Subtract"
            break;

            case "=":
                onClickName = "Equal";
            break;

            case ".":
                onClickName = "Comma";
            break;

            default:
                onClickName = value;
        }

        return <Button onClick={props[`onClick${onClickName}`]} key={value} name={value}/>
    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={props.display}>
                <Button onClick={props.onDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>

    );
}
