import {
    onAddChar,
    onDelete,
    onEqual,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject,
}
from "../utilities";

const initState = {
    displayString: "",
    calcString: "",
    value: 0,
    isCalc: false
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onEqual.getName:
            return concatObjects(state, {
                value: addValueWithCalcstring(),
                calcString: "",
                displayString: `${state.displayString} = ${(addValueWithCalcstring())}`,
                isCalc: true
            });

        case onAddChar.getName: 
        console.log(calcStringValidation);
            return concatObjects(state, {
                calcString: state.calcString + calcStringValidation(action.char),
                displayString: `${state.displayString}${action.char}`
            });

        case onAddMath.getName:
            return concatObjects(state, {
                calcString: "",
                value: addValueWithCalcstring(),
                isCalc: false
            });
        
        default:
            return state;
    }

    function addValueWithCalcstring() {
        return state.value + parseInt(state.calcString)
    }
}

function calcStringValidation(char) {
    switch(char) {
        case "-":
        case "+":
        case "/":
        case "X":
            return ""
        default:
            return char;
    }
}
