import React, {
    useState
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    let [state, setState] = useState(""); 

    const checkOperators = ({valueToCheck, valueToReturn, defaultValueToReturn}) => {
        switch(valueToCheck){
            case "/":
            case "X":
            case "-":
            case "+":
                return valueToReturn;
            default:
                return defaultValueToReturn;
        }
    }

    const validation = value => {
        value = value.toString();

        let lastCharOfState = state[state.length - 1];

        if (!lastCharOfState && !(value === ".")) {
            return checkOperators({
                valueToCheck: value, 
                valueToReturn: "",
                defaultValueToReturn: value
            })
        }

        return checkOperators({
            valueToCheck: lastCharOfState, 

            valueToReturn: checkOperators({
                valueToCheck: value, 

                valueToReturn: "",

                defaultValueToReturn: value
            }),

            defaultValueToReturn: value
        })

    }

    const fn = value => {
        value = validation(value);

        let string = `${state}${value}`;

        string = `${state}${value}`;

        setState(string);
    }

    const buttons = value => {
        let button;

        if (!(value === "=")) {
            button = <Button onClick={() => fn(value)} key={value} name={value}/>;
        }

        else {
            button = <Button onClick={props.onClickEqual} key={value} name={value}/>
        }

        return button;
    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={state}>
                <Button onClick={props.onClickDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>
    );
}
