import {
    onAddChar
}
from "./actions.js";

import {
    concatObjects
}
from "../utilities";

const initState = {
    string: "",
    value: 0
};

export default (state = initState, action) => {
    switch(action.type) {
        case onAddChar.getName: 
            return concatObjects(state, {
                string: state.string + action.char
            });
        
        default:
            return state;
    }
}
