import React from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    const addOnclickForButtons = value => {
        let propName;

        switch(value) {
            case "/":
                propName = "Divide";
            break;

            case "X":
                propName = "Multiply";
            break;

            case "+":
                propName = "Add";
            break;

            case "-":
                propName = "Subtract"
            break;

            case "=":
                propName = "Equal";
            break;

            case ".":
                propName = "Comma";
            break;

            default:
                propName = value;
        }

        return <Button key={value} name={value}/>

    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={props.display}>
                <Button onClick={props.onDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map()}
            </Grid>
        </Jumbotron>

    );
}
