import React, { 
    Component 
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

export default class extends Component {  
    layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];
    
    render() {
        return (
            <Jumbotron width="30rem" center>
                <Display text="">
                <Button name="AC"/>
                </Display>
                
                <VerticalLine/>    
                
                <Grid columns={4}>
                {this.layout.map(button => {
                    let a = <Button name={button}/>;
                    
                    if (button === "=") {
                    a = <Button onClick={this.props.onCalculate} name={button}/>
                    }

                    return a;
                })}
                </Grid>
            </Jumbotron>
        );
    }
}
