import React from "react";

export default props => {
    let styles = {
        width: "100%",
        maxWidth: `${props.width || "100%"}`
    };

    if (props.center) {
        styles = {
            ...styles,
            margin: "auto",
            top: "50%",
            transform: "translateY(50%)"

        }
    }

    if (props.right) {
        styles["margin-left"] = "auto";
    }

    return (
        <div style={styles} className="jumbotron"> 
            {props.children} 
        </div>
    );
}
