import React, {
    useState,
    useRef,
    useEffect
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

import {
   //getLastItem,
    //getFirstItem
}
from "../utilities";

export default props => {
    const layout = [
        7,  8,   9,  " / ",
        4,  5,   6,  " X ",
        1,  2,   3,  " - ",
        0, ".", "=", " + "
    ];

    let [state, setState] = useState(""); 

    const changeState = value => {
        setState(
            `${state}${value}`
            .replace(/\s/, "")
        );
    }

    const buttons = value => {
        let button;

        if (!(value === "=")) {
            button = <Button onClick={() => changeState(value)} key={value} name={value}/>;
        }

        else {
            button = <Button onClick={props.onClickEqual} key={value} name={value}/>
        }

        return button;
    }

    const input = useRef(null);

    useEffect(() => {
        console.log(input.current)
    }, []);    

    return (
        <Jumbotron width="30rem" center>
            <Display text={state} ref={input}>
                <Button onClick={props.onClickDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>
    );
};
