import {
    onAddChar,
    onDelete,
    onEqual,
    onAddMath,
    onSubtractMath,
    onDivideMath,
    onMultipleMath
}
from "./actions.js";

import {
    concatObjects,
    copyObject,
}
from "../utilities";
import { stat } from "fs";

const initState = {
    displayString: "",
    calcString: "0",
    value: 0,
    isCalc: false
};

export default (state = initState, action) => {
    switch(action.type) {
        case onDelete.getName:
            return copyObject(initState);

        case onEqual.getName:
            return state;

        case onAddChar.getName: 
            return concatObjects(state, {
                calcString: calcStringValidation(action.char),
                displayString: `${state.displayString}${action.char}`
            });

        case onAddMath.getName:
            return state;
            
        
        default:
            return state;
    }
}

function calcStringValidation(char) {
    switch(char) {
        case "-":
        case "+":
        case "/":
        case "X":
            return ""
        default:
            return char;
    }
}
