import React, {
    useState
} 
from 'react';

import {
    Jumbotron,
    VerticalLine,
    Display,
    Button,
    Grid
}
from "./bootstrap";

import {
    getLastItem
}
from "../utilities";

export default props => {
    const layout = [
        7,  8,   9,  "/",
        4,  5,   6,  "X",
        1,  2,   3,  "-",
        0, ".", "=", "+"
    ];

    let [state, setState] = useState(""); 

    const switchCase = (value, checkValues) => {
        let bool = false;

        for (const checkValue of checkValues) {
            bool = bool || (checkValue === value);
        }

        return bool
    }

    const specialCharValidation = (value) => {
        const lastCharOfState = getLastItem(state);
        
        const specialChar = ["/", "X", "-", "+", "0."];

        const isLastCharSpecial = switchCase(lastCharOfState, specialChar);

        const isValueCharSpecial = switchCase(value, specialChar);

        if (isLastCharSpecial) {
            if (isValueCharSpecial) {
                return value = "";
            }
        }

        return value;
    }

    const changeState = value => {
        value = specialCharValidation(value);

        const lastCharOfState = getLastItem(state);

        

        let string = `${state}${value}`;

        string = `${state}${value}`;

        setState(string);
    }

    const buttons = value => {
        let button;

        if (!(value === "=")) {
            button = <Button onClick={() => changeState(value)} key={value} name={value}/>;
        }

        else {
            button = <Button onClick={props.onClickEqual} key={value} name={value}/>
        }

        return button;
    }

    return (
        <Jumbotron width="30rem" center>
            <Display text={state}>
                <Button onClick={props.onClickDelete} name="AC"/>
            </Display>
            
            <VerticalLine/>    
            
            <Grid columns={4}>
                {layout.map(buttons)}
            </Grid>
        </Jumbotron>
    );
};
