import  {
    bootstrapColors
} 
from "./bootstrapColor";

export const getColorType =  (props, type) => {
    const copiedProps = {...props};
    delete copiedProps.children;

    const colorType = Object.keys(copiedProps).reduce( (acc, prop) => {
        let bool = true;
        

        for (const colorType in bootstrapColors) {
            const color = `${type}-${bootstrapColors}`;
            
            if (color === prop) {
                bool = false;        
            } 
        }

        if (bool) {
            acc.push(prop);
            return acc;
        }

        else {
            return 
        }
    }, []);

    console.log(colorType);

}
