import React, { Fragment} from "react";

import {
    CssClasses,
    addColorTypeToCssClass
} 
from "../../utilities";

const title = props => {
    
    if (props.size) {
        props.size = "1";
    }

    const classes = new CssClasses("container");
    
    addColorTypeToCssClass({
        type: "bg",
        classes: classes,
        props: props
    });

    return (
        <p>{props.children}</p>
    );
}

export default title;
