import React from "react";
import {
    Column,
    Grid,
    Row,
    Container,
    Form,
    Title,
    Previewer
} 
from "./index";

const markdownPreviewer = props => (
    <Container fluid bg-light>
        <Grid>
        <Row>
            <Column bg-success>
            <Title text-light>Previewer</Title>
            <Previewer>
                {props.children}
            </Previewer>
            </Column>
            <Column bg-dark>
            <Title text-light>Editor</Title>
            <Form onChange={props.onChange}/> 
            </Column>
        </Row>
        </Grid>
    </Container>
);

export default markdownPreviewer;
