import React from "react";

import {
    CssClasses,
    addColorTypeToCssClass,
    textPos
} 
from "../../utilities";

const title = props => {
    let size = props.size;

    if (!props.size) {
        size = "1";
    }

    const classes = new CssClasses(`h${size}`);
    
    addColorTypeToCssClass({
        type: "text",
        classes: classes,
        props: props
    });
    
    switch (props.pos) {
        case textPos.center:
        case textPos.left:
        case textPos.right:
    }

    return (
        <p {...props} className={classes.toString()}>{props.children}</p>
    );
}

export default title;
