import React from "react";

import {
    CssClasses,
    addColorTypeToCssClass
} 
from "../../utilities";

const title = props => {
    let size = props.size;

    if (!props.size) {
        size = "1";
    }

    const classes = new CssClasses("h");
    classes.concatStringToClass("h", size);
    
    addColorTypeToCssClass({
        type: "text",
        classes: classes,
        props: props
    });

    console.log(props.size);

    return (
        <p className={classes.toString()}>{props.children}</p>
    );
}

export default title;
