import React from "react";

import {
    CssClasses,
    addColorTypeToCssClass
} 
from "../../utilities";

const title = props => {
    
    if (props.size) {
        props.size = "1";
    }

    const classes = new CssClasses(props.size);
    
    addColorTypeToCssClass({
        type: "text",
        classes: classes,
        props: props
    });

    return (
        <p className={classes.toString()}>{props.children}</p>
    );
}

export default title;
