export const getColorType =  (props, type) => {
    const copiedProps = {...props};
    
    delete copiedProps.children;
    
    type = type.toLowerCase();

    const colorType = Object.keys(copiedProps).reduce( (acc, prop) => {
        if (prop.includes(type)) {
            return prop;
        }

        else {
            console.log(acc);
            acc.push(prop);
            return acc;
        }
    });

    const [, color] = colorType.split("-");

    return {
        type: type,
        color: color
    }
}
