import React from "react";

import {
    CssClasses,
    getColorType
} 
from "../../../utilities"

const column = props => {
    const classes = new CssClasses("col-lg");

    const color = getColorType(props, "bg");

    classes.addClass(color);

    return (
        <div className="col-lg">
            {props.children}
        </div>
    );
}
export default column;
