export const getColorType =  (props, type) => {
    const copiedProps = {...props};
    
    delete copiedProps.children;
    
    type = type.toLowerCase();

    const colorType = Object.keys(copiedProps).filter(
        prop => prop.includes(
            type
        )
    )[0];

    const [, color] = colorType.split("-");

    return {
        type: type,
        color: color
    }
}
