import React,{
  Component
} 
from 'react';

import Pomodoro from "./components/pomodoro";
import Timer from "./utilities/timer";
import timeStringToSeconds from "./utilities/timeStringToSeconds";

class App extends Component {
  breakTimer = new Timer();
  sessionTimer = new Timer();

  state = {
    indicator: "session",
    isSession: true
  }

  componentDidMount() {
    this.breakTimer
    .onStart(() => {
      this.setState({indicator: "break", isSession: false});
      console.log("break timer start", "indicator", this.state.indicator);
    })
    .onEnd(() => {
      console.log("break timer end")
      this.sessionTimer.start();
    })
    .onInterval(currentTime => {
      console.log("break timer inervall: ", currentTime);
    });

    this.sessionTimer
    .onStart(() => {
      this.setState({indicator: "session", isSession: true});
      console.log("session timer start", "indicator", this.state.indicator);
    })
    .onEnd(() => {
      console.log("session timer end")
      this.breakTimer.start();
    })
    .onInterval(currentTime => {
      console.log("session timer inervall: ", currentTime);
    });
  }

  onChangeBreakhHandler = event => {
    const seconds = timeStringToSeconds(event.target.value);
    this.breakTimer.setTime(seconds);
  }

  onChangeSessionHandler = event => {
    const seconds = timeStringToSeconds(event.target.value);
    this.sessionTimer.setTime(seconds);
  }

  onClickResetHandler = () => {
    this.sessionTimer.reset();
    this.breakTimer.reset();
  }

  onClickStopHandler = () => {
    if (this.state.isSession) {
      this.sessionTimer.stop();
    }
    else {
      this.breakTimer.stop();
    }
  }

  render(){
    return (
      <Pomodoro
      onChangeBreakLength={event => this.onChangeBreakhHandler(event)}
      onChangeSessionLength={event => this.onChangeSessionHandler(event)}

      onClickStart={() => this.sessionTimer.start()}
      onClickStop={() => this.onClickStopHandler()}
      onClickReset={() => this.onClickResetHandler()}
      onClickContinue={() => console.log()}

      indicator={this.state.indicator}
      display={"00:00:00"}/>
    );
  }
}

export default App;
