export default class {
    constructor(time) {
        this.time = time;
    }

    start(fn) {
        const interval = setInterval(() => {
            console.log("ibra");
        }, 1000);

        setTimeout(() => {
            fn = fn ? fn() : null;
            clearInterval(interval);
        }, this.time * 1000);
    }
}
