import React from 'react';

import Pomodoro from "./components/pomodoro";


function App() {
  const timer = ({time, end}) => {
    let timeCounter = -1;
    console.log("start");
    const inverval = setInterval(() => {
      timeCounter++;      
      console.log(time - timeCounter);

      if (timeCounter === time) {
        clearInterval(inverval);
        end = end? end(): 0;
      }
    }, 1000)
  };


  return (
    <Pomodoro
      onChangeBreakLength={() => console.log(1)}
      onChangeSessionLength={() => console.log(1)}

      onClickStart={() => console.log(1)}
      onClickStop={() => console.log(1)}
      onClickReset={() => console.log(1)}

      display={"00:00:00"}
    />
  );
}

export default App;
